if(selected(../register_method/lmp_method, 'method_edd'), date-time(decimal-date-time(../method_edd/u_edd) - 280), '')
))), "%Y-%m-%d")

round((if((decimal-date-time( /pregnancy/lmp_date_8601 ) + 12*7) > decimal-date-time(today()), decimal-date-time( /pregnancy/lmp_date_8601 ) + 12*7,
if((decimal-date-time( /pregnancy/lmp_date_8601 ) + 20*7) > decimal-date-time(today()), decimal-date-time( /pregnancy/lmp_date_8601 ) + 20*7,
if((decimal-date-time( /pregnancy/lmp_date_8601 ) + 26*7) > decimal-date-time(today()), decimal-date-time( /pregnancy/lmp_date_8601 ) + 26*7,
if((decimal-date-time( /pregnancy/lmp_date_8601 ) + 30*7) > decimal-date-time(today()), decimal-date-time( /pregnancy/lmp_date_8601 ) + 30*7,
if((decimal-date-time( /pregnancy/lmp_date_8601 ) + 34*7) > decimal-date-time(today()), decimal-date-time( /pregnancy/lmp_date_8601 ) + 34*7,
if((decimal-date-time( /pregnancy/lmp_date_8601 ) + 36*7) > decimal-date-time(today()), decimal-date-time( /pregnancy/lmp_date_8601 ) + 36*7,
if((decimal-date-time( /pregnancy/lmp_date_8601 ) + 38*7) > decimal-date-time(today()), decimal-date-time( /pregnancy/lmp_date_8601 ) + 38*7,
